import { signInWithPopup } from "firebase/auth";
import { onSnapshot } from "firebase/firestore";
import { takeLatest, put, all, call } from "redux-saga/effects";
import { auth, googleProvider, createUserProfileDocument } from "../../firebase/firebase.utils";
import { googleSignInFailure, googleSignInSuccess } from "./user.actions";


import UserActionTypes from "./user.types";

export function* signInWithGoogle() {
    try {
        const { user } = yield signInWithPopup(auth, googleProvider);
        const userRef = yield call(createUserProfileDocument, user);
        // const userSnapshot = yield call(onSnapshot, userRef);
        const userSnapshot = yield onSnapshot( userRef, (snapShot) => {
            const data = snapShot.map((doc) => ({
                id: doc.id,
                ...doc.data(),
            }));
            // return ({
            //     id: snapShot.id,
            //     ...snapShot.data()
            // });
        });

        console.log(userSnapshot.id)
        
        yield put(googleSignInSuccess({ id: userSnapshot.id, ...userSnapshot }))
        console.log('oke');
        // const channel = yield eventChannel(snapShot => onSnapshot(userRef, (snapShot)))

        console.log(user);
        console.log(userRef);
        console.log(userSnapshot)
        // console.log(take(channel));
        // yield put(googleSignInSuccess({ id: userSnapshot.id, ...userSnapshot.data() }))
    } catch (error) {
        yield put(googleSignInFailure(error))
    }
}

export function* onGoogleSignInStart() {
    yield takeLatest(UserActionTypes.GOOGLE_SIGN_IN_START, signInWithGoogle)
}

export function* userSagas() {
    yield all([
        call(onGoogleSignInStart)
    ])
}
