import React, { useState } from 'react'
import CustomButton from '../custom-button/custom-button.component'
import FormInput from '../form-input/form-input.component'
import { connect } from 'react-redux'

// import { auth, signInWithGoogle } from '../../firebase/firebase.utils'
// import { signInWithEmailAndPassword } from 'firebase/auth';
import { googleSignInStart, emailSignInStart } from '../../redux/user/user.actions';

// import './sign-in.styles.scss'
import { ButtonContainer, SignInContainer, SignInTitle } from './sign-in.styles';

const SignIn = ({ emailSignInStart, googleSignInStart }) => {
    const[userCredetials, setUserCredetials] = useState({ email: '', password: '' });
    
    const { email, password } = userCredetials
    
    const handleSubmit = async event => {
        event.preventDefault();

        emailSignInStart(email, password);
    }

    const handleChange = event => {
        const { value, name } = event.target;

        setUserCredetials({ ...userCredetials, [name]: value })
    }

    return (
        <SignInContainer>
            <SignInTitle>I already have an account</SignInTitle>
            <span>Sign in with your email and password.</span>

            <form onSubmit={handleSubmit}>
                <FormInput 
                    type="email"
                    name="email"
                    value={ email }
                    handleChange={ handleChange }
                    label="Emais"
                    required
                />
                {/* <label htmlFor="email">Email</label> */}
                <FormInput
                    type="password"
                    name="password"
                    value={ password }
                    handleChange={ handleChange }
                    label="Password"
                    required
                />
                {/* <label htmlFor="password">Password</label> */}
                <ButtonContainer>
                    <CustomButton type="submit">Sign In</CustomButton>
                    <CustomButton 
                        type="button"
                        onClick={ googleSignInStart }
                        isGoogleSignIn
                    >Sign In With Google</CustomButton>
                </ButtonContainer>
            </form>
        </SignInContainer>
    )
}

const mapDispatchToProps = dispatch => ({
    googleSignInStart: () => dispatch(googleSignInStart()),
    emailSignInStart: (email, password) => dispatch(emailSignInStart({ email, password }))
})

export default connect(null, mapDispatchToProps)(SignIn)
