import React from "react"
import { connect } from "react-redux"
import CustomButton from "../custom-button/custom-button.component"
import { addItem } from "../../redux/cart/cart.actions"

// import './collection-item.styles.scss'
import { BackgroundImage, CollectionFooterContainer, CollectionItemContainer, CollectionSpanName, CollectionSpanPrice } from "./collection-item.styles"


const CollectionItem = ({ item, addItem }) => {
    const { name, price, imageUrl } = item;

    return (
    <CollectionItemContainer>
        <BackgroundImage
            className="image"
             imageUrl={imageUrl}
            // style={{ backgroundImage: `url(${imageUrl})` }}
        />
        <CollectionFooterContainer>
            <CollectionSpanName>{ name }</CollectionSpanName>
            <CollectionSpanPrice>{ price }</CollectionSpanPrice>
        </CollectionFooterContainer>
        <CustomButton onClick={ () => addItem(item) }inverted >Add to Cart</CustomButton>
    </CollectionItemContainer>)
}

const mapDispatchToProps = dispatch => ({
    addItem: item => dispatch(addItem(item))
})

export default connect(null, mapDispatchToProps)(CollectionItem);
